def script_args(f):
    'single decorator for adding script args'
    args = [magic_arguments.argument('--out', type=str, help='The variable in which to store stdout from the script.\n            If the script is backgrounded, this will be the stdout *pipe*,\n            instead of the stderr text itself and will not be auto closed.\n            '), magic_arguments.argument('--err', type=str, help='The variable in which to store stderr from the script.\n            If the script is backgrounded, this will be the stderr *pipe*,\n            instead of the stderr text itself and will not be autoclosed.\n            '), magic_arguments.argument('--bg', action='store_true', help='Whether to run the script in the background.\n            If given, the only way to see the output of the command is\n            with --out/err.\n            '), magic_arguments.argument('--proc', type=str, help='The variable in which to store Popen instance.\n            This is used only when --bg option is given.\n            ')]
    for arg in args:
        f = arg(f)
    return f